import ObjectView from '../view/ObjectView';

import View from '../view/View';

import { parse } from '../utility/resource';

import { $ as $_,  $up as $_up,  delegate, trigger } from '../utility/DOM';

import { multipleMap } from '../utility/object';


const attr_prop = {
        class:     'className',
        for:       'htmlFor',
        readonly:  'readOnly'
    },
    event_handler = new Map();


/**
 * Utility methods of Web Component
 */
export default  class Component {
    /**
     * @type {string} - `tagName` of a Custom Element
     */
    static get tagName() {

        return this.name.replace(
            /[A-Z]/g,  char => '-' + char.toLowerCase()
        ).slice( 1 );
    }

    get [Symbol.toStringTag]() {  return this.constructor.name;  }

    /**
     * @private
     *
     * @param {String}   type
     * @param {String}   selector
     * @param {Function} handler
     */
    static on(type, selector, handler) {

        var map = event_handler.get( this );

        if (! map)  event_handler.set(this,  map = [ ]);

        map.push({type, selector, handler});
    }

    /**
     * @param {?Object} option - https://developer.mozilla.org/en-US/docs/Web/API/element/attachShadow#Parameters
     *
     * @return {HTMLElement} This custom element
     */
    buildDOM(option) {

        if (self.ShadyCSS  &&  !(ShadyCSS.nativeCss && ShadyCSS.nativeShadow))
            ShadyCSS.styleElement( this );

        const shadow = this.attachShadow({
                mode:            'open',
                delegatesFocus:  true,
                ...option
            }),
            {template, data} = this.constructor;

        if ( template )
            shadow.appendChild( document.importNode(template, true) );

        if (this.slotChangedCallback instanceof Function)
            for (let slot of this.$('slot'))
                slot.addEventListener(
                    'slotchange',  () => this.slotChangedCallback(
                        [... slot.assignedNodes()],  slot,  slot.name
                    )
                );

        if (this.viewUpdateCallback instanceof Function)
            this.shadowRoot.addEventListener('update',  event => {

                const {oldData, newData, view} = event.detail;

                if (this.viewUpdateCallback(newData, oldData, view)  ===  false)
                    event.preventDefault();
            });

        const view = new ObjectView( shadow );

        if ( view[0] )  view.render(data || { });

        const map = event_handler.get( this.constructor )  ||  '';

        for (let {type, selector, handler}  of  map)
            this.on(type,  selector,  handler.bind( this ));

        return this;
    }

    /**
     * Main view of this component
     *
     * @type {View}
     */
    get view() {  return  View.instanceOf( this.shadowRoot );  }

    /**
     * Set the getter & setter of the DOM property
     *
     * @private
     *
     * @param {string[]} attributes - Names of HTML attributes
     *
     * @return {string[]} `attributes`
     */
    static linkDataOf(attributes) {

        for (let key of attributes) {

            key = attr_prop[key] || key;

            if (! (key in this.prototype))
                Object.defineProperty(this.prototype, key, {
                    set:         function (value) {

                        this.view.commit(key, value);
                    },
                    get:         function () {

                        return  this.view.data[ key ];
                    },
                    enumerable:  true
                });
        }

        return attributes;
    }

    /**
     * Assign the new value to the DOM property
     * which has the same name of the changed attribute
     *
     * @protected
     *
     * @param {string}  name
     * @param {?string} oldValue
     * @param {?string} newValue
     *
     * @return {*} DOM property value of `newValue`
     */
    attributeChangedCallback(name, oldValue, newValue) {

        name = attr_prop[name] || name;

        switch ( newValue ) {
            case '':      return  this[ name ] = true;
            case null:    return  this[ name ] = false;
            default:      try {
                return  this[ name ] = parse( newValue );

            } catch (error) {

                return  this[ name ] = newValue;
            }
        }
    }

    /**
     * @param {string} selector - CSS selector
     *
     * @return {Element[]} Element set which matches `selector` in this Shadow DOM
     */
    $(selector) {  return  $_(selector, this.shadowRoot);  }

    /**
     * @param {string} selector - CSS selector
     *
     * @return {?Element} Matched parent
     */
    $up(selector) {  return  $_up(selector, this);  }

    /**
     * @param {string} selector - CSS selector
     *
     * @return {Element[]} Matched elements which assigned to slots
     */
    $slot(selector) {

        return multipleMap(
            multipleMap(this.$('slot'),  slot => slot.assignedNodes()),
            node => {

                if (!(node instanceof Element))  return;

                const list = [ ];

                if (node.matches( selector ))  list[0] = node;

                list.push(... $_(selector, node));

                return list;
            }
        );
    }

    /**
     * Delegate listener for DOM events
     *
     * @param {string}          type       - Name of a DOM event
     * @param {string}          [selector] - CSS selector of delegate elements
     * @param {DOMEventHandler} callback
     *
     * @return {Element} This element
     */
    on(type, selector, callback) {

        if (selector instanceof Function)  callback = selector, selector = '';

        (/^:host/.test( selector )  ?  this.shadowRoot  :  this).addEventListener(
            type,  selector ? delegate(selector, callback) : callback
        );

        return this;
    }

    /**
     * @param {String|Event} event
     * @param {?*}           detail     - Additional data
     * @param {?Boolean}     bubbles
     * @param {?Boolean}     cancelable
     * @param {?Boolean}     composed   - Whether the event will cross
     *                                    from the shadow DOM into the standard DOM
     *                                    after reaching the shadow root
     * @return {Boolean} Event be canceled or not
     */
    trigger(event, detail, bubbles, cancelable, composed) {

        return  trigger(this, event, detail, bubbles, cancelable, composed);
    }
}

/**
 * @typedef {function(event: Event): *} DOMEventHandler
 */

import { watchAttributes } from '../utility/DOM';

import Component from './Component';


const CSS_map = {
        readonly:  {
            cursor:  'default'
        },
        disabled:  {
            cursor:          'not-allowed',
            'point-events':  'none'
        }
    },
    attributeChanged = Component.prototype.attributeChangedCallback;


/**
 * Base class for Form field components
 */
export default  class InputComponent extends HTMLElement {
    /**
     * @param {?Object} option - https://developer.mozilla.org/en-US/docs/Web/API/element/attachShadow#Parameters
     */
    constructor(option) {

        super().buildDOM( option );
    }

    /**
     * @protected
     */
    slotChangedCallback() {

        const origin = this.$slot('input')[0];

        if (! origin)  return;

        origin.style.setProperty('display', 'none', 'important');

        watchAttributes.call(
            this,
            origin,
            [
                'type', 'name', 'value',
                'readonly', 'disabled', 'checked', 'placeholder'
            ],
            this.changedPropertyOf
        );

        this.on(
            'input',
            `:host input[type="${origin.type}"]`,
            ({ target })  =>  origin.value = target.value
        ).on(
            'change',
            `:host input[type="${origin.type}"]`,
            this.trigger.bind( this )
        );
    }

    /**
     * Common behavior of Form field attributes
     *
     * @param {string}  attribute
     * @param {?string} oldValue
     * @param {?string} newValue
     */
    changedPropertyOf(attribute, oldValue, newValue) {

        if ((attribute === 'type')  &&  !newValue)  newValue = 'text';

        newValue = attributeChanged.call(
            this.view, attribute, oldValue, newValue
        );

        const style = CSS_map[ attribute ];

        if ( style )
            for (let [key, value]  of  Object.entries( style ))
                this.style[
                    newValue ? 'setProperty' : 'removeProperty'
                ](
                    `--input-${key}`, value
                );
    }

    /**
     * @type {string}
     */
    get defaultValue() {  return this.getAttribute('value');  }
}

import Template from '../view/Template';


/**
 * @type {Promise}
 */
export const documentReady = new Promise(resolve => {

    document.addEventListener('DOMContentLoaded', resolve);

    self.addEventListener('load', resolve);

    (function check() {

        (document.readyState === 'complete')  ?
            resolve()  :  setTimeout( check );
    })();
});


/**
 * jQuery-like selector
 *
 * @param {string}                            selector
 * @param {Element|Document|DocumentFragment} [context=document]
 *
 * @return {Element[]}
 */
export function $(selector, context) {

    return  [... (context || document).querySelectorAll( selector )];
}


/**
 * @param {string} selector - CSS selector
 * @param {Node}   context
 *
 * @return {?Element} Matched parent
 */
export function $up(selector, context) {

    while (context = context.parentNode)
        if (context.matches  &&  context.matches( selector ))
            return context;
}


function mediaLoad(media, condition) {

    return  new Promise((resolve, reject) => {

        if ( condition() )
            resolve();
        else
            media.onload = resolve,
            media.onerror = reject;
    });
}

/**
 * @param {Element|Document} [context]
 *
 * @return {Promise} Resolved when all media elements in `context` are loaded
 */
export function mediaReady(context) {

    return Promise.all($(
        'img[src], iframe[src], audio[src], video[src]',  context
    ).map(media => {

        if (Template.Expression.test( media.getAttribute('src') ))  return;

        switch ( media.tagName.toLowerCase() ) {
            case 'img':
                return  mediaLoad(media, () => media.complete);
            case 'iframe':
                return  new Promise((resolve, reject) => {
                    try {
                        if (media.contentDocument.readyState === 'complete')
                            resolve();
                        else
                            media.onload = resolve,
                            media.onerror = reject;

                    } catch (error) {  resolve();  }
                });
            case 'audio':
            case 'video':
                return  mediaLoad(media, () => (media.readyState > 0));
        }
    }));
}


/**
 * @param {Element} element
 *
 * @return {number} The index of `element` in its siblings
 */
export function indexOf(element) {

    var index = 0;

    while (element = element.previousElementSibling)  index++;

    return index;
}


/**
 * @param {Event} event
 *
 * @return {Element} The target of `event` object (**Shadow DOM** is in account)
 */
export function targetOf(event) {

    const target = event.composedPath ? event.composedPath() : event.path;

    return  (target || '')[0]  ||  event.target;
}


/**
 * DOM event delegate
 *
 * @param {string}          selector
 * @param {DOMEventHandler} handler
 *
 * @return {Function} Handler wrapper
 */
export function delegate(selector, handler) {

    return  function (event) {

        var target = targetOf( event );

        if (! target.matches( selector ))
            target = $up(selector, target);

        if ( target )
            return  handler.call(target, event, target, event.detail);
    };
}


/**
 * @param {Element}      element
 * @param {String|Event} event
 * @param {?*}           detail     - Additional data
 * @param {?Boolean}     bubbles
 * @param {?Boolean}     cancelable
 * @param {?Boolean}     composed   - Whether the event will cross
 *                                    from the shadow DOM into the standard DOM
 *                                    after reaching the shadow root
 * @return {Boolean} Event be canceled or not
 */
export function trigger(element, event, detail, bubbles, cancelable, composed) {

    return element.dispatchEvent(
        (event instanceof Event)  ?
            new event.constructor(event.type, {
                bubbles:     event.bubbles,
                cancelable:  event.cancelable
            }) :
            new CustomEvent(event, {
                bubbles, cancelable, composed, detail
            })
    );
}


/**
 * @param {string} markup - Code of an markup fragment
 *
 * @return {DocumentFragment}
 */
export function parseDOM(markup) {

    const box = document.createElement('template');

    box.innerHTML = markup;

    return box.content;
}


/**
 * @param {*} DOM
 *
 * @return {Boolean}
 */
export function isHTML(DOM) {

    return  (DOM instanceof HTMLDocument)  ||
        (DOM instanceof DocumentFragment)  ||
        (DOM instanceof HTMLElement);
}


const serializer = new XMLSerializer(),
    documentXML = document.implementation.createDocument(null, 'xml');

function stringOf(document) {

    if (document instanceof HTMLDocument)
        for (let element of $(
            'style:not(:empty), script:not(:empty)',  document
        ))
            if ( element.textContent.trim() )
                element.firstChild.replaceWith(
                    documentXML.createCDATASection( element.textContent )
                );

    return  serializer.serializeToString( document );
}

/**
 * @param {Node} fragment
 *
 * @return {string} HTML/XML source code
 */
export function stringifyDOM(fragment) {

    if ((fragment instanceof HTMLDocument)  ||  !isHTML( fragment ))
        return stringOf( fragment );

    if (fragment instanceof HTMLElement)  return fragment.outerHTML;

    const box = document.createElement('template');

    box.content.append( fragment.cloneNode( true ) );

    return box.innerHTML;
}


const sandbox = document.createElement('div');

/**
 * @param {String} source - Markup source code
 *
 * @return {String} Special characters are tranformed to Markup entries
 */
export function encodeMarkup(source) {

    sandbox.textContent = source;

    return sandbox.innerHTML;
}

/**
 * @param {String} source
 *
 * @return {String} Markup entries are tranformed back to Special characters or
 *                  Escape Markup tags
 */
export function decodeMarkup(source) {

    sandbox.innerHTML = source;

    return sandbox.textContent;
}


/**
 * @typedef {Function} AttributeWatcher
 *
 * @param {string}  name
 * @param {?string} oldValue
 * @param {?string} newValue
 */

/**
 * @param {Element}          element
 * @param {string[]}         names
 * @param {AttributeWatcher} callback
 *
 * @return {MutationObserver}
 */
export function watchAttributes(element, names, callback) {

    const observer = new MutationObserver(list => {

        for (let mutation of list)
            callback.call(
                this,
                mutation.attributeName,
                mutation.oldValue,
                element.getAttribute( mutation.attributeName )
            );
    });

    observer.observe(element, {
        attributes:         true,
        attributeOldValue:  true,
        attributeFilter:    names
    });

    for (let attribute of element.attributes)
        callback.call(this, attribute.name, null, attribute.value);

    return observer;
}


/**
 * @param {number} [second=0]
 *
 * @return {Promise} Wait seconds in Macro tasks
 */
export function delay(second) {

    return  new Promise(resolve  =>  setTimeout(resolve, (second || 0) * 1000));
}


var tick;
/**
 * @return {Promise<number>} [Time stamp](https://developer.mozilla.org/en-US/docs/Web/API/DOMHighResTimeStamp)
 */
export function nextTick() {

    return  tick || (
        tick = new Promise(
            resolve => self.requestAnimationFrame(
                time  =>  (tick = null, resolve( time ))
            )
        )
    );
}

/**
 * @param {*} object
 *
 * @return {string}
 */
export function classNameOf(object) {

    return  Object.prototype.toString.call( object ).slice(8, -1);
}


/**
 * @param {*}      object
 * @param {string} key    - Property name
 *
 * @return {?Object} https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Object/getOwnPropertyDescriptor#Description
 */
export function getPropertyDescriptor(object, key) {

    var descriptor;  object = Object.create( object );

    while (object = Object.getPrototypeOf( object ))
        if (descriptor = Object.getOwnPropertyDescriptor(object, key))
            return descriptor;
}


/**
 * @typedef {Object} DecoratorDescriptor
 *
 * @property {String}                kind         - `class`, `field` or `method`
 * @property {String}                [key]        - Member name
 * @property {String}                [placement]  - `static` or `prototype`
 * @property {Object}                [descriptor] - Last parameter of `Object.defineProperty()`
 * @property {DecoratorDescriptor[]} [elements]   - Class members
 */

/**
 * @param {Function|Object}   target                          - Class or its prototype
 * @param {String}            key                             - Member name
 * @param {Function|Object|*} value                           - `{ set, get }` for Field accessors
 * @param {Object}            [descriptor={enumerable: true}] - Use for `Object.defineProperty()`
 *
 * @return {DecoratorDescriptor}
 */
export function decoratorOf(target, key, value, descriptor = {enumerable: true}) {

    descriptor = {
        key, descriptor,
        placement:  (target instanceof Function) ? 'static' : 'prototype'
    };

    if (value instanceof Function)
        descriptor.kind = 'method',  descriptor.descriptor.value = value;
    else if (
        (value.constructor === Object)  &&
        ((value.set || value.get) instanceof Function)
    )
        descriptor.kind = 'method',  Object.assign(descriptor.descriptor, value);
    else
        descriptor.kind = 'field',  descriptor.initializer = () => value;

    return descriptor;
}


const Array_iterator = [ ][Symbol.iterator];

/**
 * @param {Object} arrayLike
 *
 * @return {Iterable} `arrayLike`
 */
export function toIterable(arrayLike) {

    if (!(arrayLike[Symbol.iterator] instanceof Function))
        arrayLike[Symbol.iterator] = Array_iterator;

    return arrayLike;
}


/**
 * Iteratable decorator for Class, Method or Getter
 *
 * @param {DecoratorDescriptor} meta
 */
export function arrayLike(meta) {

    const descriptor = meta.descriptor;

    switch ( meta.kind ) {
        case 'class':
            meta.elements.push(
                decoratorOf({ }, Symbol.iterator, Array_iterator)
            );
            break;
        case 'method':
            for (let key  of  ['value', 'get']) {

                let origin;

                if (origin = descriptor[key])
                    descriptor[key] = function () {

                        return  toIterable( origin.apply(this, arguments) );
                    };
            }
    }
}


/**
 * Equivalent to the integration of Array's map() & filter() methods
 *
 * @param {Iterable}                                           list
 * @param {function(item: *, index: number, list:Iterable): *} filter
 *     - Return `item` itself to reserve, `undefined` or `null` to ignore, or Array to merge in.
 *
 * @return {Array}
 */
export function multipleMap(list, filter) {

    toIterable( list );

    filter = (filter instanceof Function)  &&  filter;

    var result = [ ], i = 0;

    for (let item of list) {

        if ( filter )  item = filter(item, i, list);

        if (item != null)
            result.push[
                (item instanceof Array)  ?  'apply'  :  'call'
            ](result, item);
    }

    return result;
}


function notEqual(A, B) {  return  A !== B;  }

/**
 * Return `true` means that A is not equal B
 *
 * @typedef {function(A: *, B: *): Boolean} UniqueComparer
 */

/**
 * @param {Array}                        list        - Original array
 * @param {String|Symbol|UniqueComparer} [condition]
 *
 * @return {Array} Deduplicated new array
 */
export function unique(list, condition) {

    switch (typeof condition) {
        case 'string':
        case 'symbol':
            condition = ((key, A, B) => A[key] !== B[key]).bind(null, condition);
            break;
        case 'function':
            break;
        default:
            condition = notEqual;
    }

    const result = [ ];

    function uniqueWith(item) {

        for (let i = 0, length = result.length >>> 0;  i < length;  i++)
            if (!condition(item, result[i]))  return false;

        return true;
    }

    for (let i = 0, l = list.length >>> 0;  i < l;  i++)
        if ((i === 0)  ||  uniqueWith( list[i] ))
            result.push( list[i] );

    return result;
}


/**
 * Merge own properties of two or more objects together into the first object
 * by their descriptor
 *
 * @param {Object}    target - An object that will receive the new properties
 *                             if `source` are passed in
 * @param {...Object} source - Additional objects containing properties to merge in
 *                             (Value of `null` or `undefined` will be skipped)
 *
 * @return {Object} The `target` parameter
 */
export function extend(target, ...source) {

    for (let object of source)  if (object instanceof Object) {

        let descriptor = Object.getOwnPropertyDescriptors( object );

        for (let key  of  Object.keys( descriptor ))
            if (
                ('value' in descriptor[key])  &&
                !(descriptor[key].value != null)
            )
                delete descriptor[key];

        if (object instanceof Function) {

            delete descriptor.name;
            delete descriptor.length;
            delete descriptor.prototype;

            let prototype = Object.getOwnPropertyDescriptors( object.prototype );

            delete prototype.constructor;

            Object.defineProperties(target.prototype, prototype);
        }

        Object.defineProperties(target, descriptor);
    }

    return target;
}


var depth = 0;

/**
 * Traverse Object-tree
 *
 * @param {Object} node     - Object tree
 * @param {String} fork_key - Key of children list
 *
 * @yield {Object}
 * @property {?Object} node   - Current node
 * @property {Object}  parent - Parent node
 * @property {Number}  index  - Index of current level
 * @property {Number}  depth  - Level count of current node
 */
export function* mapTree(node, fork_key) {

    const children = node[fork_key];    depth++ ;

    for (var i = 0;  i < children.length;  i++) {

        yield {parent: node,  node: children[i],  index: i,  depth};

        if ((children[i] != null)  &&  (children[i][fork_key] || '')[0])
            yield* mapTree(children[i], fork_key);
    }

    depth-- ;
}

import { $, stringifyDOM, parseDOM } from './DOM';


/**
 * @param {String} raw
 *
 * @return {Number} Length in Half-width characters
 */
export function byteLength(raw) {

    return  raw.replace(/[^\u0021-\u007e\uff61-\uffef]/g, 'xx').length;
}


/**
 * @param {string|URL} URI - Full URL of a resource
 *
 * @return {boolean} Whether it's cross domain to current page
 */
export function isXDomain(URI) {

    return  ((new URL(URI, document.baseURI)).origin  !==  self.location.origin);
}


/**
 * @param {Element} form - `<form />` or `<fieldset />`
 *
 * @return {String|FormData|Object}
 */
export function serialize(form) {

    if ( $('input[type="file"][name]', form)[0] )  return new FormData( form );

    const data = Array.from(
        form.elements,
        field  =>  (field.name && [field.name, field.value])
    ).filter( Boolean );

    if (
        (form.form || form).getAttribute('enctype') !== 'application/json'
    )
        return  '' + new URLSearchParams( data );

    form = { };

    for (let [key, value]  of  data)  form[key] = value;

    return form;
}


/**
 * @param {Object} data
 *
 * @return {String} JSON source code
 */
export function stringify(data) {

    return  JSON.stringify(data,  (key, value) => {

        if (value instanceof Node)  return stringifyDOM( value );

        return value;
    }, 4);
}


/**
 * @param {String} raw - JSON source code
 *
 * @return {Object}
 */
export function parse(raw) {

    return  JSON.parse(raw,  (key, value) => {

        if (/^\d{4}(-\d{2}){2}T\d{2}(:\d{2}){2}\.\d{3}Z$/.test( value ))
            return  new Date( value );

        if (/<[\w-][\s\S]*?>/.test( value )) {

            const node = parseDOM( value );

            return  (node.childNodes.length < 2)  ?  node.firstChild  :  node;
        }

        return value;
    });
}


/**
 * HTTP request
 *
 * @param {string}                URI            - HTTP URL
 * @param {string}                [method='GET']
 * @param {string|Object|Element} [body]         - Data to send
 * @param {Object}                [headers]
 * @param {Object}                [option]       - https://developer.mozilla.org/en-US/docs/Web/API/WindowOrWorkerGlobalScope/fetch#Parameters
 *
 * @return {string|Object|DocumentFragment|Blob} Parse response data automatically
 */
export async function request(URI, method = 'GET', body, headers, option) {

    if (body instanceof Element)  body = serialize( body );

    if (body instanceof Object)  try {

        body = stringify( body.valueOf() );

        headers = headers || { };

        headers['Content-Type'] = headers['Content-Type'] || 'application/json';

    } catch (error) {/* eslint-disable-line */}

    const response = await self.fetch(URI, {
        method,  headers,  body,
        mode:         isXDomain( URI ) ? 'cors' : 'same-origin',
        credentials:  'same-origin',
        ... option
    });

    const type = response.headers.get('Content-Type').split(';')[0];

    switch ( type ) {
        case 'text/html':
            return  parseDOM(await response.text());
        case 'application/json':
            return  await response.json();
        default:
            return  await response[
                (type.split('/')[0] === 'text')  ?  'text'  :  'blob'
            ]();
    }
}


/**
 * @param {File}   file
 * @param {String} [type='DataURL']   - https://developer.mozilla.org/en-US/docs/Web/API/FileReader#Methods
 * @param {String} [encoding='UTF-8'] - https://developer.mozilla.org/en-US/docs/Web/API/FileReader/readAsText#Parameters
 *
 * @return {String|ArrayBuffer}
 */
export function readAs(file, type = 'DataURL', encoding = 'UTF-8') {

