import View from '../../source/view/View';

import ObjectView from '../../source/view/ObjectView';

import Sinon from 'sinon';

import { nextTick } from '../../source/utility/DOM';

var HTML, fragment = `
    <fieldset>
        <legend title="Test field">
            <label>Test</label>
            <input type="checkbox">
        </legend>
    </fieldset>`.trim();


/**
 * @test {ObjectView}
 */
describe('ObjectView()',  () => {

    before(()  =>  HTML = readFileSync('test/ObjectView/index.html') + '');

    describe('Single view',  () => {

        var view, element;

        before(() => {

            element = JSDOM.fragment( HTML ).querySelector('fieldset');

            element.remove();
        });

        /**
         * @test {ObjectView#scan}
         */
        it('Scan DOM',  () => {

            view = new ObjectView( element );

            view.should.have.length( 3 );
        });

        /**
         * @test {ObjectView#watch}
         * @test {ObjectView#render}
         */
        it('Render data',  () => {

            view.render({
                name:    'Test',
                title:   'Test field',
                enable:  true,
                extra:   'test'
            });

            view.extra.should.be.equal('test');

            view.toString().should.be.equal( fragment );

            view.content.querySelector('input').checked.should.be.true();
        });

        /**
         * @test {ObjectView#valueOf}
         */
        it('Get data',  () => {

            view.valueOf().should.be.eql( view.data );

            view.valueOf().should.not.be.equal( view.data );
        });

        /**
         * @test {ObjectView#commit}
         * @test {ObjectView#watch}
         */
        it('Async render',  async () => {

            view.render = Sinon.spy( view.render );

            view.commit('name', '<b>Example</b>');

            view.title = 'Example field';

            view.render.should.not.be.called();

            view.toString().should.be.equal( fragment );

            await nextTick();

            view.render.should.be.calledOnce();

            view.toString().should.be.equal(
                fragment.replace(/Test(?= )/, 'Example')
                    .replace('Test', '<b>Example</b>')
            );
        });
    });

    describe('Nested view',  () => {

        var view;

        /**
         * @test {ObjectView#scan}
         */
        it('Scan DOM',  () => {

            view = new ObjectView( HTML );

            view.should.have.length( 4 );

            view[3].should.be.instanceof( ObjectView );
        });

        /**
         * @test {View#bindWith}
         * @test {View.instanceOf}
         * @test {ObjectView#watch}
         */
        it('Associate DOM',  () => {

            View.instanceOf( view.content[0] ).should.be.equal( view );

            view.tips.parent.should.be.equal( view );
        });

        /**
         * @test {ObjectView#render}
         */
        it('Render data',  () => {

            view.render({
                name:    'Test',
                title:   'Test field',
                enable:  true,
                tips:    {
                    title:    'Test tips',
                    content:  'Test content'
                }
            });

            view.toString().trim().should.be.equal(`
<form>
    ${fragment}
    <dl data-object="tips">
        <dt>Test tips</dt>
        <dd>Test content</dd>
    </dl>
</form>`.trim()
            );
        });

        /**
         * @test {ObjectView#valueOf}
         */
        it('Get data',  () => {

            view.valueOf().should.be.eql( view.data );

            view.valueOf().should.not.be.equal( view.data );

            view.data.tips.should.be.equal( view.tips.data );

            view.tips.scope.should.be.equal( view.data );
        });
    });
});

import Template from '../source/view/Template';

import sinon from 'sinon';


/**
 * @test {Template}
 */
describe('Template',  () => {

    /**
     * @test {Template#parse}
     * @test {Template#clear}
     */
    it('Parsing',  () => {

        const template = new Template(
            '[ ${(new Date()).getFullYear()} ]  Hello, ${this.name} !'
        );

        template.should.have.length( 2 );

        template.reference.get('this')[0].should.be.equal('name');

        template.toString().should.be.equal(
            `[ ${(new Date()).getFullYear()} ]  Hello,  !`
        );
    });

    /**
     * @test {Template#evaluate}
     */
    it('Evaluate text',  () => {

        const template = new Template(
            '[ ${this.time} ]  Hello, ${scope.creator}\'s ${view.name} !',
            ['view', 'scope']
        );

        template.should.have.length( 3 );

        template.reference.size.should.be.equal( 3 );

        template.evaluate(
            {time: '2015-07-23'},  {name: 'EasyWebApp.js'},  {creator: 'TechQuery'}
        ).should.be.equal(
            '[ 2015-07-23 ]  Hello, TechQuery\'s EasyWebApp.js !'
        );
    });

    /**
     * @test {Template#evaluate}
     */
    it('Evaluate non-text',  () => {

        function method() { }

        (new Template('${this.method}')).evaluate({ method })
            .should.be.equal( method );
    });

    /**
     * @test {Template#onChange}
     */
    it('Changed callback',  () => {

        const empty = `[ ${(new Date()).getFullYear()} ]  Hello,  !`;

        const onChange = sinon.spy((newValue, oldValue, ...bindData)  =>  {

            if (oldValue === null)
                newValue.should.be.equal( empty );
            else {
                oldValue.should.be.equal( empty );

                newValue.should.be.equal(
                    `[ ${(new Date()).getFullYear()} ]  Hello, EWA !`
                );
            }

            bindData.should.be.eql([1, 2, 3]);
        });

        const template = new Template(
            '[ ${(new Date()).getFullYear()} ]  Hello, ${this.name} !',
            onChange,
            [1, 2, 3]
        );

        template.evaluate({name: 'EWA'});

        onChange.should.have.callCount(2);
    });
});

