import {
    getPropertyDescriptor, multipleMap, extend, mapTree, decoratorOf
} from '../../source/utility/object';


describe('Object utility',  () => {
    /**
     * @test {getPropertyDescriptor}
     */
    describe('Get the property descriptor',  () => {

        const object = {test: 1},
            descriptor = {
                value:         1,
                writable:      true,
                enumerable:    true,
                configurable:  true
            };

        it(
            'Own property',
            ()  =>  getPropertyDescriptor(object,  'test')
                .should.be.eql( descriptor )
        );

        it(
            'Property on the prototype chain',
            ()  =>  getPropertyDescriptor(
                Object.create( object ),  'test'
            ).should.be.eql( descriptor )
        );
    });

    /**
     * @test {multipleMap}
     */
    describe('Map & filter',  () => {

        it('Filter `null` & `undefined`',  () => {

            multipleMap(
                [null, undefined, NaN, false, 0, 1],  item => item
            ).should.be.eql([
                NaN, false, 0, 1
            ]);
        });

        it('Merge Array elements into result',  () => {

            multipleMap([[1], [2, 3]],  item => item).should.be.eql([1, 2, 3]);
        });
    });

    /**
     * @test {extend}
     */
    describe('Mixin objects',  () => {

        it('Ordinary objects',  () => {

            Object.keys(extend(
                { },  null,  {
                    example:   1,
                    get test() { },
                    null:       null,
                    undefined:  undefined
                }
            )).should.be.eql([
                'example', 'test'
            ]);
        });

        it('Function or Class',  () => {

            function Test() { }

            class Example {
                constructor(test) {  test;  }

                static test() { }

                test() { }
            }

            extend(Test, Example).should.have.properties({
                name:    'Test',
                length:  0,
                test:    Example.test
            });

            Test.prototype.should.not.be.equal( Example );

            Test.prototype.should.have.property('test', Example.prototype.test);
        });
    });

    /**
     *  @test {mapTree}
     */
    it('Filter object tree',  () => {

        mapTree({
            id:        0,
            children:  [
                {
                    id:        1,
                    children:  [
                        {
                            id:        3,
                            children:  []
                        }
                    ]
                },
                {
                    id:        2,
                    children:  []
                }
            ]
        },  'children',  function (child, index, depth) {

            if (depth < 2)  return child.id;

        }).should.be.eql([1, 2]);
    });

    /**
     * @test {decoratorOf}
     */
    describe('Create decorator descriptor',  () => {

        it('Static fields',  () => {

            const descriptor = {
                kind:        'field',
                key:         'example',
                placement:   'static'
            };

            const meta = decoratorOf(class Test { },  'example',  'sample');

            meta.should.containEql( descriptor );

            meta.descriptor.enumerable.should.be.true();

            meta.initializer().should.be.equal('sample');
        });

        it('Static methods',  () => {

            const descriptor = {
                kind:        'method',
                key:         'example',
                placement:   'static'
            };

            const meta = decoratorOf(class Test { },  'example',  () => 'sample',  {
                enumerable:  false
            });

            meta.should.containEql( descriptor );

            meta.descriptor.enumerable.should.be.false();

            meta.descriptor.value().should.be.equal('sample');
        });

        it('Static accessors',  () => {

            const descriptor = {
                kind:        'method',
                key:         'example',
                placement:   'static'
            };

            const meta = decoratorOf(class Test { },  'example',  {get: () => 'sample'});

            meta.should.containEql( descriptor );

            meta.descriptor.get().should.be.equal('sample');
        });

        it('Prototype methods',  () => {

            const descriptor = {
                kind:        'method',
                key:         'example',
                placement:   'prototype'
            };

            const meta = decoratorOf(
                (class Test { }).prototype,  'example',  () => 'sample'
            );

            meta.should.containEql( descriptor );

            meta.descriptor.value().should.be.equal('sample');
        });
    });
});

import JSDOM from '../DOM-polyfill';

import {
    serialize, request, fileTypeOf, blobFrom
} from '../../source/utility/resource';

import WebServer from 'koapache';

import { readFileSync } from 'fs';


describe('Resource utility',  () => {
    /**
     * @test {serialize}
     */
    describe('Serialize <form /> or <fieldset />',  () => {

        const form = JSDOM.fragment(`
<form enctype="application/json">
    <input type="text" name="text" value="example">
    <textarea name="content">sample</textarea>
    <input type="file" name="file">
    <input type="submit">
</form>`
        ).children[0];

        it(
            'Multiple part data',
            ()  =>  serialize( form ).should.be.instanceOf( FormData )
        );

        it('JSON',  () => {

            form.elements.file.remove();

            serialize( form ).should.be.eql({
                text:     'example',
                content:  'sample'
            });
        });

        it('URL encode',  () => {

            form.removeAttribute('enctype');

            serialize( form ).should.be.equal('text=example&content=sample');
        });
    });

    /**
     * @test {request}
     */
    describe('HTTP request',  () => {

        var server;

        before(async () => {

            server = await (new WebServer()).workerHost();

            server = `http://${server.address}:${server.port}`;
        });

        it('Get Plain text',  () => request(
            `${server}/ReadMe.md`,
        ).should.be.fulfilledWith(
            readFileSync('ReadMe.md') + ''
        ));

        it('Get JSON object',  () => request(
            `${server}/package.json`,
        ).should.be.fulfilledWith(
            JSON.parse( readFileSync('package.json') )
        ));

        it(
            'Get HTML document',
            async ()  =>  (await request(
                `${server}/test/ObjectView/index.html`,
            )).should.be.class(
                'DocumentFragment'
            )
        );

        it(
            'Get Binary file',
            async ()  =>  (await request(`${server}/docs/image/github.png`))
                .should.be.class('Blob')
        );
    });

    describe('Binary',  () => {

        const data = `data:image/png;base64,${
            readFileSync('docs/image/github.png').toString('base64')
        }`;

        /**
         * @test {fileTypeOf}
         */
        it(
            'Parse Data URI',
            ()  =>  fileTypeOf( data ).should.be.fulfilledWith({
                schema:  'data',
                type:    'png'
            })
        );

        /**
         * @test {blobFrom}
         */
        it(
            'Convert DataURI to Blob',
            ()  =>  blobFrom( data ).should.be.class('Blob')
        );
    });
});
