        if (! (buffer = view_buffer.get( this )))
            view_buffer.set(this,  buffer = { });

        buffer[key] = value;

        await nextTick();

        if (! view_buffer.get( this ))  return;

        this.render( buffer );

        view_buffer.delete( this );
